const   defaultConfig   =   { 
   iceServers :   [{ 
     urls :   'stun:stun.webrtc.ecl.ntt.com:3478' , 
     url :    'stun:stun.webrtc.ecl.ntt.com:3478' , 
   }], 
   iceTransportPolicy :   'all' ,  };

// デバッグ情報を最大(3)にして接続する場合  const   peer   =   new   Peer ({ 
   key :     "<YOUR-API-KEY>" 
   debug :   3 ,  });   // TURNサーバを強制利用する場合  const   peer   =   new   Peer ({ 
   key :     "<YOUR-API-KEY>" 
   debug :   3 , 
   config :   { 
     iceTransportPolicy :   'relay' , 
   },  });

// 自身のlocalStreamを設定して、相手に発信する場合  const   call   =   peer . call ( 'peerID' ,   localStream );   // 自身のlocalStreamおよびmetadataを設定して、相手に発信する場合  const   call   =   peer . call ( 'peerID' ,   localStream ,   { 
   metadata :   { 
     foo :   'bar' , 
   }  });   // 映像コーデックとしてH264を利用する場合  const   call   =   peer . call ( 'peerID' ,   localStream ,   { 
   videoCodec :   'H264' ,  });   // 音声のみ接続先から受信する設定で、相手に発信する場合  const   call   =   peer . call ( 'peerID' ,   { 
   audioReceiveEnabled :   true ,  });

// 単にDataChannelを接続する場合(デフォルトで信頼性有り)  peer . connect ( 'peerId' );   // metadata付きでconnectする場合  peer . connect ( 'peerId' ,   { 
   metadata :   { 
     hoge :   "foobar" , 
   }  });   // 信頼性無しモードでDataChannelを接続する場合  peer . connect ( 'peerId' ,   { 
   dcInit :   { 
     // 最大2回、再送する 
     maxRetransmits :   2 , 
   },  });

// Mesh接続を利用する場合  const   room   =   peer . joinRoom ( "roomName" ,   { 
   mode :   'mesh' ,  
   stream :   localStream ,  });   // SFU接続を利用する場合  const   room   =   peer . joinRoom ( "roomName" ,   { 
   mode :   'sfu' ,  
   stream :   localStream ,  });

peer . listAllPeers ( peers   =>   { 
   console . log ( peers ) 
   // => ["yNtQkNyjAojJNGrt", "EzAmgFhCKBQMzKw9"]  });

peer . on ( 'call' ,   call   =>   { 
   // 着信側のメディアストリームを設定して応答 
   call . answer ( mediaStream );  });

// 仮にRoom名を指定せずにjoinRoomを呼んだ場合  peer . on ( 'error' ,   error   =>   { 
   console . log ( ` ${ error . type } :  ${ error . message } ` ); 
   // => room-error: Room name must be defined.  });

// 発信側  mediaConnection   =   peer1 . call ( 'peerID' ,   mediaStream );  // 着信側  peer2 . on ( 'call' ,   call   =>   { 
   // peer2の持つメディアストリームで応答する 
   call . answer ( mediaStream2 )  });

// 接続元が metadata: { foo: 'bar' } を設定した場合  peer . on ( 'call' ,   call   =>   { 
   console . log ( call . metadata ); 
   // => {foo: "bar"}  });

peer . on ( 'connection' ,   connection   =>   { 
   // metadataが付与されていた場合 
   console . log ( connection . metadata ); 
   // => connect時に付与した、metadataを参照する  });

// 'hello world'という文字列を送付します。   dataConnection . send ( 'hello world' );  // 受信側では、次のように発火します。  dataConnection . on ( 'data' ,   data   =>   { 
   console . log ( 'hello world' ); 
   // => 'hello world'  });

dataConnection . on ( 'data' ,   data   =>   { 
   console . log ( 'hello world' ); 
   // => 'hello world'  });

meshRoom . on ( 'removeStream' ,   stream   =>   { 
   // 削除されたストリームを持つPeerIDを取得 
   const   peerId   =   stream . peerId ;  }

sfuRoom . on ( 'removeStream' ,   stream   =>   { 
   // 削除されたストリームを持つPeerIDを取得 
   const   peerId   =   stream . peerId ;  }

const   defaultConfig   =   { 
   iceServers :   [{ 
     urls :   'stun:stun.webrtc.ecl.ntt.com:3478' , 
     url :    'stun:stun.webrtc.ecl.ntt.com:3478' , 
   }], 
   iceTransportPolicy :   'all' ,  };

// Connect ECLWebRTC signaling server with full debug option.  const   peer   =   new   Peer ({ 
   key :     "<YOUR-API-KEY>" 
   debug :   3 ,  });   // Force turn server  const   peer   =   new   Peer ({ 
   key :     "<YOUR-API-KEY>" 
   debug :   3 , 
   config :   { 
     iceTransportPolicy :   'relay' , 
   },  });

// Call a peer, providing our mediaStream  const   call   =   peer . call ( 'peerID' ,   localStream );   // Call a peer, providing our mediaStream and metadata  const   call   =   peer . call ( 'peerID' ,   localStream ,   { 
   metadata :   { 
     foo :   'bar' , 
   }  });   // Call a peer, providing our mediaStream with H264(video codec)  const   call   =   peer . call ( 'peerID' ,   localStream ,   { 
   videoCodec :   'H264' ,  });   // Call a peer, and just want to receive audio  const   call   =   peer . call ( 'peerID' ,   { 
   audioReceiveEnabled :   true ,  });

// connect with data channel and with reliable mode(default)  peer . connect ( 'peerId' );   // with metadata  peer . connect ( 'peerId' ,   { 
   metadata :   { 
     hoge :   "foobar" , 
   }  });   // connect with data channel and with unreliable mode  peer . connect ( 'peerId' ,   { 
   dcInit :   { 
     maxRetransmits :   2 , 
   },  });

const   room   =   peer . joinRoom ( "roomName" ,   { 
   mode :   'mesh' ,  
   stream :   localStream ,  });   const   room   =   peer . joinRoom ( "roomName" ,   { 
   mode :   'sfu' ,  
   stream :   localStream ,  });

peer . listAllPeers ( peers   =>   { 
   console . log ( peers ) 
   // => ["yNtQkNyjAojJNGrt", "EzAmgFhCKBQMzKw9"]  });

peer . on ( 'call' ,   call   =>   { 
   // answer with media stream 
   call . answer ( mediaStream );  });

// When calling joinRoom() without room name  peer . on ( 'error' ,   error   =>   { 
   console . log ( ` ${ error . type } :  ${ error . message } ` ); 
   // => room-error: Room name must be defined.  });

// Calling party  mediaConnection   =   peer1 . call ( 'peerID' ,   mediaStream );  // Called party  peer2 . on ( 'call' ,   call   =>   { 
   // answer with called party's media stream. 
   call . answer ( mediaStream2 )  });

// When calling party set `metadata: { foo: 'bar' }`  peer . on ( 'call' ,   call   =>   { 
   console . log ( call . metadata ); 
   // => {foo: "bar"}  });

peer . on ( 'connection' ,   connection   =>   { 
   console . log ( connection . metadata ); 
   // => Show metadata which calling party added  });

// Send 'hello world' string.  dataConnection . send ( 'hello world' );  // 'data' events fires on received side  dataConnection . on ( 'data' ,   data   =>   { 
   console . log ( 'hello world' ); 
   // => 'hello world'  });

dataConnection . on ( 'data' ,   data   =>   { 
   console . log ( 'hello world' ); 
   // => 'hello world'  });

meshRoom . on ( 'removeStream' ,   stream   =>   { 
     // e.g. getting the peer ID who removed stream 
   const   peerId   =   stream . peerId ;  }

sfuRoom . on ( 'removeStream' ,   stream   =>   { 
   // e.g. getting the peer ID who removed stream 
   const   peerId   =   stream . peerId ;  }
